            for(i=0; i<=ng+nh-1; i++)
            {
                alglib.ap.assert((double)(fscales[i+1])>(double)(0), "UnscaleAndCheckNLCViolation: integrity check failed");
                v = fi[i+1]*fscales[i+1];
