                    apserv.tracevectorautoprec(monitor.nonc0lngrep.x0, 0, monitor.n, _params);
                    alglib.ap.trace("\n");
                    alglib.ap.trace("*** D  = ");
                    apserv.tracevectorautoprec(monitor.nonc0lngrep.d, 0, monitor.n, _params);
                    alglib.ap.trace("\n");
                }
            }
            if( monitor.rep.nonc1suspected && monitor.rep.nonc1test0positive )
