            optserv.smoothnessmonitorinit(state.smonitor, state.s, 0, 0, false, _params);
            state.diffstep = diffstep;
            state.n = n;
            state.m = m;
            allocatemem = flags%2==0;
            flags = flags/2;
            if( allocatemem )
            {
                apserv.rvectorsetlengthatleast(ref state.rho, m, _params);
