                lcsrcidx = new int[0];
                hasbndl = new bool[0];
                hasbndu = new bool[0];
                scaledbndl = new double[0];
                scaledbndu = new double[0];
                x = new double[0];
                fi = new double[0];
                j = new double[0,0];
                meritstate = new minsqpmeritphasestate();
                step0x = new double[0];
                stepkx = new double[0];
                backupx = new double[0];
                step0fi = new double[0];
