            public double[] au;
            public presolveinfo()
            {
                init();
            }
            public override void init()
            {
                rawbndl = new double[0];
                rawbndu = new double[0];
                colscales = new double[0];
                rowscales = new double[0];
                c = new double[0];
                bndl = new double[0];
                bndu = new double[0];
