        /*************************************************************************
        This object stores linear solver state.
        You should use functions provided by MinLP subpackage to work with this
        object
        *************************************************************************/
        public class minlpstate : apobject
        {
            public int n;
            public int algokind;
            public double ipmlambda;
            public double[] s;
            public double[] c;
            public double[] bndl;
            public double[] bndu;
            public int m;
            public sparse.sparsematrix a;
            public double[] al;
