                apserv.unserializerealarray(s, ref p.al, _params);
                apserv.unserializerealarray(s, ref p.au, _params);
            }
            k = s.unserialize_int();
            alglib.ap.assert(k==872, "LPTestProblemUnserialize: end-of-stream marker not found");
        }


